import React from 'react';

const Input = (props) => {
    return(
        <div>
            <label
                htmlFor={props.for}
            >
                {props.label}
            </label>
            <input
                className={`${props.className}`}
                type={props.type || 'text'}
                value={props.value}
                placeholder={props.placeholder}
                id={props.id}
                name={props.name}
            />
        </div>
    );
};

export default Input;
