import React, { useState, useEffect } from 'react';
import useHttp from '../../../hooks/use-http';

import MealItem from '../MealItem/MealItem';
import Card from '../../UI/Card/Card';

import styles from './AvailableMeals.module.css';

const AvailableMeals = (props) => {
  const [meals, setMeals] = useState([]);

  const { isLoading, error, sendRequest: fetchMeals } = useHttp();

  useEffect(() => {
    const requestConfig = {
      url: 'https://react-https-request-cfd6e-default-rtdb.firebaseio.com/meals.json'
    };

    const dataTransform = (data) => {
      const loadedMeals = [];
  
      for (const mealKey in data) {
        loadedMeals.push({ 
          id: mealKey, 
          name: data[mealKey].name,
          description: data[mealKey].description,
          price: data[mealKey].price
        });
      }
  
      setMeals(loadedMeals);
    }

    fetchMeals(requestConfig, dataTransform);
  }, [fetchMeals]);

  let content;

  if(isLoading) {
    content = <li>Loading...</li>
  }

  if(!isLoading) {
    content = meals.map(
                  meal => (
                      <MealItem
                        key={meal.id}
                        id={meal.id}
                        name={meal.name}
                        description={meal.description}
                        price={meal.price}
                      />
                  )
              )
  }

  if(!isLoading && error) {
    content = <li></li>;
  }

  return(
      <section className={styles.meals}>
          <Card>
            <ul>
              {content}
            </ul>
          </Card>
      </section>
  );
};

export default AvailableMeals;
