import { useState, useEffect } from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredNameIsInvalid, setEnteredNameIsInvalid] = useState(false);
  const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);
  const [enteredNameBlur, setEnteredNameBlur] = useState(false);

  useEffect(() => {
    if(enteredNameIsInvalid) {
      console.log('Name is valid');
    }
  }, [enteredNameIsInvalid]);

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
    if(event.target.trim().length === 0) {
      setEnteredNameIsInvalid(true)
      return;
    } else {
      enteredNameIsInvalid(false);
    }
  }

  const submitFormHandler = (event) => {
    event.preventDefault();
    if(enteredName.trim().length === 0) {
      setEnteredNameIsInvalid(true)
      return;
    }
  };

  return (
    <form onSubmit={submitFormHandler}>
      <div className='form-control'>
        <label htmlFor='name'>Your Name</label>
        <input className='uche' type='text' id='name' value={enteredName} onChange={nameInputHandler}/>
        {enteredNameIsInvalid && <span style={{ 'color': 'red' }}>Name is invalid</span>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
