import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import useHttp from './hooks/use-http';
import Notification from './components/UI/Notification';

import { useSelector, useDispatch } from 'react-redux';
import { useEffect, Fragment } from 'react';
import { cartActions } from './store/cart';

function App() {
  const cartState = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  // if(isLoading) {
  //   dispatch(cartActions.showNotification({
  //     status: 'Sending...',
  //     title: 'Sending...',
  //     message: 'Sending cart data!'
  //   }));
  // }

  // if(!isLoading && error) {
  //   dispatch(cartActions.showNotification({
  //     status: 'error',
  //     title: 'Error!',
  //     message: 'Sending cart data failed!'
  //   }));
  // }

  // if(!isLoading && !error) {
  //   dispatch(cartActions.showNotification({
  //     status: 'success',
  //     title: 'Success!',
  //     message: 'Sent cart data successfully!'
  //   }));
  // }

  useEffect(() => {
    const requestConfig = {
      url: 'https://movie-http-21f07-default-rtdb.firebaseio.com/cart.json',
      method: 'PUT',
      headers: {'content-type': 'application/json'},
      body: {cartState}
    }
    const {isLoading, error, sendRequest} = useHttp();
    sendRequest(requestConfig, () => {});

    if(isLoading) {
      dispatch(cartActions.showNotification({
        status: 'Sending...',
        title: 'Sending...',
        message: 'Sending cart data!'
      }));
    }
  }, [cartState, sendRequest]);

  return (
    <Fragment>
      {cartState.notification &&
        <Notification 
          status={cartState.notification.status}
          title={cartState.notification.title}
          message={cartState.notification.message}
        />
      }
      <Layout>
        {cartState.showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>

  );
}

export default App;
