import { createStore } from 'redux';

const defaultState = {
    counter: 0
};

const counterReducer = (state = defaultState, action) => {
    switch(action.type) {
        case 'increment':
            return {
                counter: state.counter + action.amount
            }
        case 'decrement':
            return {
                counter: state.counter - 1
            }
        default:
            return state;
    }
}

const store = createStore(counterReducer);

export default store;
