import React, { useState, createContext } from 'react';

const AuthContext = createContext({
    isLoggedIn: false,
    onLogout: () => {}
});

export const AuthContextProvider = (props) => {
    const [isLoggedIn] = useState();
    return (
        <AuthContext.Provider>
            {props.children}
        </AuthContext.Provider>
    );
}

export default AuthContext;
