import React, { useRef, useEffect } from 'react';

const Input = (props) => {
    const inputRef = useRef();
    console.log(inputRef);
    useEffect(() => {
        inputRef.current.focus();
    }, []);

    return (
        <input
            ref={inputRef}
            className={`${props.className}`}
            type={props.type}
            id={props.id}
            value={props.value}
            onChange={props.onChange}
            onBlur={props.onBlur}
        />
    );
};

export default Input;
