import React, { Fragment, useState, useEffect } from 'react';

import Login from './components/Login/Login';
import Home from './components/Home/Home';
import MainHeader from './components/MainHeader/MainHeader';

function App(props) {
  return (
    <Fragment>
      <AuthContextProvider>
        <MainHeader/>
        <main>
          {!props.isLoggedIn && <Login onLogin={props.loginHandler} />}
          {props.isLoggedIn && <Home onLogout={props.logoutHandler} />}
        </main>
      </AuthContextProvider>
    </Fragment>
  );
}

export default App;
